% ============================================================
% RNG k-Îµ model (Yakhot & Orszag, 1986)
% ============================================================
\subsection*{RNG \textit{k}-Îµ model}
\begin{subequations}
\label{eq:RNG}
\begin{align}
\underbrace{\frac{\partial(\rho k)}{\partial t}
          +\nabla\cdot(\rho\vec{u}k)}_{\text{transient + advection}}
&=\underbrace{\nabla\cdot\!\Bigl[\Bigl(\mu+\frac{\mu_t}{\sigma_k}\Bigr)\nabla k\Bigr]}_{\text{diffusion}}
 +\underbrace{P_k}_{\text{production}}
 -\underbrace{\rho\varepsilon}_{\text{destruction}},
 \label{eq:RNG_k}\\[4pt]
\underbrace{\frac{\partial(\rho\varepsilon)}{\partial t}
          +\nabla\cdot(\rho\vec{u}\varepsilon)}
&=\nabla\cdot\!\Bigl[\Bigl(\mu+\frac{\mu_t}{\sigma_\varepsilon}\Bigr)\nabla\varepsilon\Bigr]
 +C_{1\varepsilon}\frac{\varepsilon}{k}P_k
 -C_{2\varepsilon}^*\rho\frac{\varepsilon^2}{k}.
 \label{eq:RNG_e}
\end{align}
\end{subequations}
Turbulent viscosity
$\displaystyle
\mu_t=C_\mu\rho\frac{k^2}{\varepsilon},
\quad
C_\mu=0.084\;5,\;
\sigma_k=1.39,\;
\sigma_\varepsilon=1.39.
$
The effective coefficient
$\displaystyle
C_{2\varepsilon}^*=C_{2\varepsilon}+\frac{C_\mu\eta^3(1-\eta/\eta_0)}{1+\beta\eta^3},\;
\eta=\frac{k}{\varepsilon}S,\;
S=\sqrt{2\mskip2mu\mathrm{tr}(\mathbf{S}^2)},\;
\eta_0=4.38,\;
\beta=0.012.
$

% ============================================================
% Realizable k-Îµ model (Shih et al., 1995)
% ============================================================
\subsection*{Realizable \textit{k}-Îµ model}
\begin{subequations}
\label{eq:Realizable}
\begin{align}
\frac{\partial(\rho k)}{\partial t}+\nabla\cdot(\rho\vec{u}k)
&=\nabla\cdot\!\Bigl[\Bigl(\mu+\frac{\mu_t}{\sigma_k}\Bigr)\nabla k\Bigr]
 +P_k+P_b
 -\rho\varepsilon
 -Y_M,
 \label{eq:Real_k}\\[4pt]
\frac{\partial(\rho\varepsilon)}{\partial t}+\nabla\cdot(\rho\vec{u}\varepsilon)
&=\nabla\cdot\!\Bigl[\Bigl(\mu+\frac{\mu_t}{\sigma_\varepsilon}\Bigr)\nabla\varepsilon\Bigr]
 +\rho C_1 S\varepsilon
 -\rho C_2\frac{\varepsilon^2}{k+\sqrt{\nu\varepsilon}}.
 \label{eq:Real_e}
\end{align}
\end{subequations}
Model constants
$\displaystyle
\sigma_k=1.0,\;
\sigma_\varepsilon=1.2,\;
C_2=1.9,\;
C_1=\max\!\Bigl[0.43,\,\frac{\eta}{\eta+5}\Bigr],\;
\eta=\frac{k}{\varepsilon}S.
$
The dilatation term
$\displaystyle
Y_M=2\rho\varepsilon\mathrm{Ma}_t^2,\;
\mathrm{Ma}_t=\sqrt{\frac{2k}{a^2}}\;
(a\;\text{is speed of sound}).
$
